mapper.insert:
INSERT INTO TBL_BOOK 
					(
						BK_ISBN         
					  , BK_NM           
					  , BK_WTR          
					  , BK_PBLSR        
					  , BK_RLS          
					  , BK_SUB          
					  , BK_CTG_C        
					  , BK_CTG_P        

					  ) VALUES (
					  				 #{bookIsbn}
					  			   , #{bookNm}
					  			   , #{bookWtr}
					  			   , #{publisher}
					  			   , #{releaseDate}
					  			   , #{booksub}
					  			   , #{cCtgIdx}
					  			   , #{cCtgPnt}
					  			)

mapper.update:
UPDATE TBL_BOOK 
SET	   BK_NM    = #{bookNm}     
	 , BK_WTR   = #{bookWtr}    
	 , BK_PBLSR = #{publisher}  
	 , BK_RLS   = #{releaseDate}
	 , BK_SUB   = #{booksub}    
	 , BK_CTG_C = #{cCtgIdx}   
	 , BK_CTG_P = #{cCtgPnt}   
WHERE  BK_ISBN  = #{bookIsbn}
