mapper.insert:
INSERT INTO BOOK 
					(
						BK_ISBN         /* 도서 isbn*/
					  , BK_NM           /* 도서명*/   
					  , BK_WTR          /* 저자*/    
					  , BK_PBLSR        /* 출판사*/   
					  , BK_RLS          /* 출간일*/   
					  , CATEGORY        /* 카테고리*/  
					  , BK_SUB          /* 도서 소개글*/

					  ) VALUES (
					  				 #{bookIsbn}
					  			   , #{bookNm}
					  			   , #{bookWtr}
					  			   , #{publisher}
					  			   , #{releaseDate}
					  			   , #{category}
					  			   , #{booksub}
					  			)

mapper.update:
UPDATE BOOK 
SET	   BK_NM    = #{bookNm}     
	 , BK_WTR   = #{bookWtr}    
	 , BK_PBLSR = #{publisher}  
	 , BK_RLS   = #{releaseDate}
	 , CATEGORY = #{category}   
	 , BK_SUB   = #{booksub}    
WHERE  BK_ISBN  = #{bookIsbn}

UPDATE BOOK 
SET	   ISBORROW  = (1)    
WHERE  BK_ISBN  = #{bookIsbn}
